import vSelect from "vue-select";
export default {
  components: {
    "v-select": vSelect,
  },
  data() {
    return {
      countries: [],
      states: [],
      churchInfo: {},
      imgpreview: false,
      churchimg: "",
      imageDataUpload: "",
      previewUrl: "",
      church: {
        name: "",
        state: "",
        region: "",
        countryCode: "",
        phoneNumber: "",
        email: "",
        countryId: "",
        address: "",
        zone: "",
        pastor: "",
        website: "",
        domain: "",
        streamURL: "",
        // New Fields
        contactPhoneNumber: "",
        setupFeeNaira: "",
        setupFeeDollar: "",
        subscriptionFeeDollar: "",
        subscriptionFeeNaira: "",
        mobileDomain: "",
        androidUrl: "",
        iosUrl: "",
      },
    };
  },
  mounted() {
    this.openLoadingInDiv();
    this.getChurchDetails();
    this.$store
      .dispatch("getCountries")
      .then((resp) => {
        this.countries = resp.data;
        this.$vs.loading.close("#div-with-loading > .con-vs-loading");
      })
      .catch((err) => {
        this.$vs.loading.close("#div-with-loading > .con-vs-loading");
      });
  },
  methods: {
    openLoadingInDiv() {
      this.$vs.loading({
        container: "#div-with-loading",
        scale: 0.6,
      });
    },
    handleFileChange: function (event) {
      //   console.log(event.target.files[0]);
      const file = event.target.files[0];
      this.imageDataUpload = file;
      this.previewUrl = URL.createObjectURL(file);
      this.imgpreview = true;
      //   this.church.Logo = event.target.files[0];
    },
    saveImageUpload() {
      this.openLoadingInDiv();
      let churchData = {
        logo: this.imageDataUpload,
        churchId: this.$route.query._id,
      };
      this.$store
        .dispatch("updateChurchLogo", churchData)
        .then((resp) => {
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");
          this.$vs.notify({
            title: "Church Logo",
            text: "Church Logo updated successfully!",
            color: "success",
          });
          this.getChurchDetails();
          this.imgpreview = false;
          //   this.churchInfo = resp.data;
        })
        .catch((err) => {
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");
          //   console.log(err.response);
          if (err.response) {
            if (err.response.status == 400) {
              // console.log(err.response.data);
              this.$vs.notify({
                title: "Church Logo",
                text: err.response.data,
                color: "warning",
              });
            } else {
              this.$vs.notify({
                title: "Church Logo",
                text: "Unable to edit church logo, try again!",
                color: "warning",
              });
            }
          } else {
            this.$vs.notify({
              title: "Church Account",
              text: "Unable to edit church logo, try again!",
              color: "warning",
            });
          }
        });
    },
    getChurchDetails() {
      this.openLoadingInDiv();
      const churchId = this.$route.params.id;

      this.$store
        .dispatch("getChurchDetail", churchId)
        .then((resp) => {
          // this.$vs.loading.close("#div-with-loading > .con-vs-loading");
          this.churchInfo = resp.data;
          this.church.name = this.churchInfo.name;
          this.church.countryId = this.churchInfo.countryId;
          this.church.state = this.churchInfo.state;
          this.church.region = this.churchInfo.region;
          this.church.countryCode = this.churchInfo.countryCode;
          this.church.phoneNumber = this.churchInfo.phoneNumber;
          this.church.email = this.churchInfo.email;
          this.church.address = this.churchInfo.address;
          this.church.zone = this.churchInfo.zone;
          this.church.pastor = this.churchInfo.pastor;

          // New Details
          this.church.website = this.churchInfo.website;
          this.church.domain = this.churchInfo.userId.domain;
          this.church.iosUrl = this.churchInfo.userId.iosUrl;
          this.church.androidUrl = this.churchInfo.userId.androidUrl;
          this.church.mobileDomain = this.churchInfo.userId.mobileDomain;

          this.church.setupFeeNaira = this.churchInfo.setupFeeNaira;
          this.church.streamURL = this.churchInfo.streamURL;

          if (this.churchInfo.contactPhoneNumber) {
            this.church.contactPhoneNumber = this.churchInfo.contactPhoneNumber;
          }
          this.church.setupFeeDollar = this.churchInfo.setupFeeDollar;
          this.church.subscriptionFeeNaira = this.churchInfo.subscriptionFeeNaira;
          this.church.subscriptionFeeDollar = this.churchInfo.subscriptionFeeDollar;

          this.churchimg =
            "https://d2meuqd6gjekk6.cloudfront.net/" + this.churchInfo.img;
          //    this.church.transCost = this.churchInfo.pastor;

          this.$store
            .dispatch("getState", this.church.countryId)
            .then((resp) => {
              this.states = resp.data;
              this.$vs.loading.close("#div-with-loading > .con-vs-loading");
            })
            .catch((err) => {
              this.$vs.loading.close("#div-with-loading > .con-vs-loading");
            });
        })
        .catch((err) => {
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");
          // console.log(err.response);

          if (err.response) {
            if (err.response.status == 400) {
              // console.log(err.response.data);
              this.$vs.notify({
                title: "Church Account",
                text: err.response.data,
                color: "warning",
              });
            } else {
              this.$vs.notify({
                title: "Church Account",
                text: "Unable to load church details, try again!",
                color: "warning",
              });
            }
          } else {
            this.$vs.notify({
              title: "Church Account",
              text: "Unable to load church details, try again!",
              color: "warning",
            });
          }
        });
    },
    changeCountry(value) {
      this.openLoadingInDiv();
      let countryId = value._id;
      this.$store
        .dispatch("getState", countryId)
        .then((resp) => {
          this.states = resp.data;
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");
        })
        .catch((err) => {
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");
        });
    },
    changeImage() {
      document.getElementById("fileUpload").click();
      //   console.log(event.target.files[0]);
      //   this.church.Logo = event.target.files[0];
    },
    submitForm() {
      // this.church.state = this.church.state.name;
      // this.church.countryId = this.church.countryId._id;
      this.openLoadingInDiv();

      if (this.church.state.name) {
        this.church.state = this.church.state.name;
      }

      let editChurchData = {
        church: this.church,
        churchId: this.$route.query._id,
      };
      this.$store
        .dispatch("editChurch", editChurchData)
        .then((resp) => {
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");
          this.$vs.notify({
            title: "Edit Church Account",
            text: "Church Account editted successfully!",
            color: "success",
          });
          //   console.log(resp);
        })
        .catch((err) => {
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");

          if (err.response) {
            if (err.response.status == 400) {
              // console.log(err.response.data);
              this.$vs.notify({
                title: "Create Church Account",
                text: err.response.data,
                color: "warning",
              });
            } else {
              this.$vs.notify({
                title: "Edit Church Account",
                text: "Unable to edit Church account, try again!",
                color: "warning",
              });
            }
          } else {
            this.$vs.notify({
              title: "Edit Church Account",
              text: "Unable to edit Church account, try again!",
              color: "warning",
            });
          }
        });
    },
  },
};
