import vSelect from "vue-select";
export default {
  components: {
    "v-select": vSelect,
  },
  data() {
    return {
      blog: {},
      setting: {
        trialStatus: false,
        trialDays: "",
        trialPromoCode: "",
      },
    };
  },
  mounted() {
    this.getSettings();
  },
  methods: {
    openLoadingInDiv() {
      this.$vs.loading({
        container: "#div-with-loading",
        scale: 0.6,
      });
    },
    getSettings() {
      this.$store
        .dispatch("getSettings")
        .then((resp) => {
          this.setting.trialStatus = resp.data.trialStatus;
          this.setting.trialDays = resp.data.trialDays;
          this.setting.trialPromoCode = resp.data.trialPromoCode;
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");
        })
        .catch((err) => {
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");

          if (err.response) {
            if (err.response.status == 400) {
              //        console.log(err.response.data);
              this.$vs.notify({
                title: "Get App Settings",
                text: err.response.data,
                color: "warning",
              });
            } else {
              this.$vs.notify({
                title: "Get App Settings",
                text: "Unable to Get App Settings, try again!",
                color: "warning",
              });
            }
          } else {
            this.$vs.notify({
              title: "Get App Settings",
              text: "Unable to Get App Settings, try again!",
              color: "warning",
            });
          }
        });
    },
    submitForm() {
      this.openLoadingInDiv();
      let settingsN = this.setting;
      this.$store
        .dispatch("saveSettings", settingsN)
        .then((resp) => {
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");
          this.$vs.notify({
            title: "Save App Settings",
            text: "App Settings successfully updated!",
            color: "success",
          });
        })
        .catch((err) => {
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");

          if (err.response) {
            if (err.response.status == 400) {
              //        console.log(err.response.data);
              this.$vs.notify({
                title: "Save App Settings",
                text: err.response.data,
                color: "warning",
              });
            } else {
              this.$vs.notify({
                title: "Save App Settings",
                text: "Unable to Save App Settings, try again!",
                color: "warning",
              });
            }
          } else {
            this.$vs.notify({
              title: "Save App Settings",
              text: "Unable to Save App Settings, try again!",
              color: "warning",
            });
          }
        });
    },
  },
};
