export default {
  data() {
    return {
      Notifications: [],
      notificationPopUp: false,
      prevNotif: {},
      churchEmail: "",
    };
  },
  mounted() {
    this.openLoadingInDiv();
    this.getNotifications();
  },
  methods: {
    clearNotification(id) {
      this.notificationPopUp = false;
      this.openLoadingInDiv();
      this.$store
        .dispatch("clearNotification", id)
        .then((resp) => {
          //   console.log(resp);
          this.getNotifications();
          this.$vs.notify({
            title: "Clear Notification",
            text: "Notification Cleared",
            color: "success",
          });
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");
        })
        .catch((err) => {
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");
          if (err.response) {
            if (err.response.status == 400) {
              console.log(err.response.data);
              this.$vs.notify({
                title: "Clear Notification",
                text: err.response.data,
                color: "warning",
              });
            } else {
              this.$vs.notify({
                title: "Clear Notification",
                text: "Unable to Clear Notification, try again!",
                color: "warning",
              });
            }
          } else {
            this.$vs.notify({
              title: "Clear Notification",
              text: "Unable to Clear Notification, try again!",
              color: "warning",
            });
          }
        });
    },
    clearAll() {
      this.openLoadingInDiv();
      this.$store
        .dispatch("clearNotifications")
        .then((resp) => {
          this.Notifications = [];
          //   console.log(resp);
          this.$vs.notify({
            title: "Clear Notifications",
            text: "All Notifications Cleared",
            color: "success",
          });
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");
        })
        .catch((err) => {
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");
          if (err.response) {
            if (err.response.status == 400) {
              console.log(err.response.data);
              this.$vs.notify({
                title: "Clear Notifications",
                text: err.response.data,
                color: "warning",
              });
            } else {
              this.$vs.notify({
                title: "Clear Notifications",
                text: "Unable to Clear Notifications, try again!",
                color: "warning",
              });
            }
          } else {
            this.$vs.notify({
              title: "Clear Notifications",
              text: "Unable to Clear Notifications, try again!",
              color: "warning",
            });
          }
        });
    },
    elapsedTime(startTime) {
      const x = new Date(startTime);
      const now = new Date();
      let timeDiff = now - x;
      timeDiff /= 1000;

      const seconds = Math.round(timeDiff);
      timeDiff = Math.floor(timeDiff / 60);

      const minutes = Math.round(timeDiff % 60);
      timeDiff = Math.floor(timeDiff / 60);

      const hours = Math.round(timeDiff % 24);
      timeDiff = Math.floor(timeDiff / 24);

      const days = Math.round(timeDiff % 365);
      timeDiff = Math.floor(timeDiff / 365);

      const years = timeDiff;

      if (years > 0) {
        return `${years + (years > 1 ? " Years " : " Year ")}ago`;
      } else if (days > 0) {
        return `${days + (days > 1 ? " Days " : " Day ")}ago`;
      } else if (hours > 0) {
        return `${hours + (hours > 1 ? " Hrs " : " Hour ")}ago`;
      } else if (minutes > 0) {
        return `${minutes + (minutes > 1 ? " Mins " : " Min ")}ago`;
      } else if (seconds > 0) {
        return seconds + (seconds > 1 ? " sec ago" : "just now");
      }

      return "Just Now";
    },
    ReadNotification(ntf, index) {
      this.notificationPopUp = true;
      this.churchEmail = this.Notifications[index].churchId.email;
      this.prevNotif = ntf;
      this.Notifications[index].read.push(this.prevNotif._id);
      this.readReceipt(this.prevNotif._id);
    },
    readReceipt(id) {
      this.$store
        .dispatch("readNotification", id)
        .then((resp) => {})
        .catch((err) => {});
    },
    getNotifications() {
      this.$store
        .dispatch("getNotifications")
        .then((resp) => {
          this.Notifications = resp.data.data;

          console.log(this.Notifications);
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");
        })
        .catch((err) => {
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");
          if (err.response) {
            if (err.response.status == 400) {
              console.log(err.response.data);
              this.$vs.notify({
                title: "Load Notifications",
                text: err.response.data,
                color: "warning",
              });
            } else {
              this.$vs.notify({
                title: "Load Notifications",
                text: "Unable to Load Notifications, try again!",
                color: "warning",
              });
            }
          } else {
            this.$vs.notify({
              title: "Load Notifications",
              text: "Unable to Load Notifications, try again!",
              color: "warning",
            });
          }
        });
    },
    openLoadingInDiv() {
      this.$vs.loading({
        container: "#div-with-loading",
        scale: 0.6,
      });
    },
  },
};
