import vSelect from "vue-select";
export default {
  components: {
    "v-select": vSelect,
  },
  data() {
    return {
      blog: {},
    };
  },
  mounted() {
    this.getBlogPost();
  },
  methods: {
    openLoadingInDiv() {
      this.$vs.loading({
        container: "#div-with-loading",
        scale: 0.6,
      });
    },
    getBlogPost() {
      this.blog = JSON.parse(localStorage.getItem("postPrev"));
    },
    submitForm() {
      this.openLoadingInDiv();
      let blogPost = this.blog;
      this.$store
        .dispatch("createBlogPost", blogPost)
        .then((resp) => {
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");
          this.$vs.notify({
            title: "Create Blog Post",
            text: "New Post created successfully!",
            color: "success",
          });
          this.blog = {
            title: "",
            body: "",
            file: "",
          };
        })
        .catch((err) => {
          this.$vs.loading.close("#div-with-loading > .con-vs-loading");

          if (err.response) {
            if (err.response.status == 400) {
              //        console.log(err.response.data);
              this.$vs.notify({
                title: "Create Blog Post",
                text: err.response.data,
                color: "warning",
              });
            } else {
              this.$vs.notify({
                title: "Create Blog Post",
                text: "Unable to Create Blog Post, try again!",
                color: "warning",
              });
            }
          } else {
            this.$vs.notify({
              title: "Create Blog Post",
              text: "Unable to Create Blog Post, try again!",
              color: "warning",
            });
          }
        });
    },
  },
};
